/**
 * This Speaker Detection algorithm is based on https://tokbox.com/developer/guides/customize-ui/js/#active_speaker
 * Some values had to be modified for better detection of the speaker as well as once they stop speaking
 */
/// <reference types="node" />
import { EventEmitter } from 'events';
declare type SpeakerChangeDetected = {
    isSpeaking: boolean;
};
declare type Activity = {
    timestamp: number;
    talking: boolean;
};
declare interface SpeakerDetection {
    emit(event: 'activeSpeakerStatusChanged', payload: SpeakerChangeDetected): boolean;
    on(event: 'activeSpeakerStatusChanged', listener: (payload: SpeakerChangeDetected) => void): this;
}
declare class SpeakerDetection extends EventEmitter {
    activity: Activity;
    onAudioLevelUpdated(audioLevel: number): void;
    reset(): void;
}
export default SpeakerDetection;
